    /**
     * 五格五行1、2木；3、4火；5、6土；7、8金；9、0水。两位数以上的数字看最后一位数定属性，如25属土；287属金，40010属水。
     * @param  [type] $num [五格的数字]
     * @return [type]      [description]
     */
    public function getFiveWH($num){
        $endnum = substr($num,-1,1); 
        if($endnum == 1 || $endnum == 2){
            $numwh = '木';  
        }else if($endnum == 3 || $endnum == 4){
            $numwh = '火';  
        }else if($endnum == 5 || $endnum == 6){
            $numwh = '土';  
        }else if($endnum == 7 || $endnum == 8){
            $numwh = '金';  
        }else if($endnum == 9 || $endnum == 0){
            $numwh = '水';  
        }else{
            $numwh = '木';
        }
        return $numwh;
    }

