    public function getScore($arraywhjx, $scjx){
        $score = 0;
        if($scjx == '凶'){
            $score += 3;
        }else if($scjx == '小吉'){
            $score += 4;
        }else if($scjx == '中吉'){
            $score += 4;
        }else{
            $score += 5;
        }
        $countwhjx = count($arraywhjx);
        for($i=0; $i<$countwhjx; $i++){
            switch($arraywhjx[$i]){
                case '凶':
                    $score += 18; 
                    break;
                 case '半吉':
                    $score += 19; 
                    break;                               
                case '吉':
                    $score += 19; 
                    break;
                default:
                    break;
            }
        }
        return $score;
    }

